<div class="content product_header">
<div class="content_bg">
{% if product.metafields.mitosaya_fields.figure_youtube %}
<iframe class="lazyload bgimg" width="560" height="315" data-src="https://www.youtube.com/embed/{{ product.metafields.mitosaya_fields.figure_youtube }}" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>

{% elsif product.metafields.mitosaya_fields.figure_video %}
<video preload="none" data-autoplay loop playsinline muted class="lazyload bgimg">
<source type="video/mp4" src="{{ product.metafields.mitosaya_fields.figure_video | file_url }}">
</video>
{% elsif product.metafields.mitosaya_fields.figure_image %}
<img src="{{ product.metafields.mitosaya_fields.figure_image | file_url }}" class="lazyload bgimg">
{% endif %}</div><div class="content_inner">
<div class="content_body -center">
<div class="product_header_labels">
<p class="product_header_labels_main">SHOP</p>
{% if product.collections %}
<ul class="product_header_collections">
{% for collection in product.collections %}
<li>{{ collection.title }}</li>
{% endfor %}
</ul>
{% endif %}
</div>
{% if product.metafields.mitosaya_fields.summary %}
<div class="product_header_summary">
{{ product.metafields.mitosaya_fields.summary | newline_to_br }}
</div>
{% endif %}
<div class="product_header_titles">
<h1 class="product_header_titles_main">{{ product_title }}</h1>
{% if product_title_sub %}
<p class="product_header_titles_sub">{{ product_title_sub }}</p>
{% endif %}
</div>
{% if product.metafields.mitosaya_fields.alcohol %}
<p class="product_header_alcohol">{{ product.metafields.mitosaya_fields.alcohol }}% vol</p>
{% endif %}
{% if product.variants %}
<ul class="product_header_variants">
{% for variant in product.variants %}
<li>{% if variant.title != 'Default Title' %}{{ variant.title }} {% endif %}{{ variant.price | money }}
{% if variant.available %}{{ 'products.product.on_sale' | t }}{% else %}{{ 'products.product.sold_out' | t }}{% endif %}</li>
{% endfor %}
</ul>
{% endif %}
</div></div></div>

